import './search_page.css';
import { Element } from "../../components/element";
import plantImage from '../../images/1.png';

export const SearchPage = () => {
    const ListPlants = [
        {
            id:1,
            plantName: "a",
            plantImage: plantImage
        },
        {
            id:2,
            plantName: "a",
            plantImage: plantImage
        }
    ]
    const [filter, setFilter] = useState("all");
    const [searchQuery, setSearchQuery] = useState("");

    const filteredPlants = ListPlants.filter((place) =>
        ListPlants.placeTitle.toLowerCase().includes(searchQuery.toLowerCase()) &&
        (filter === "all" ? true : ListPlants.type === filter)
      );
    const handleSelect = (id) => {
        console.log('Select', id);
      };
    return(
        <div className="search_page">
            <p className="serach_title">Product Search</p>
            {filteredPlants.map((plant) => (
          <Element
          key={plant.id}
          plantTitle= {plant.plantName}
          plantImage = {plant.plantImage} 
          plantClick = {handleSelect(plant.id)}
          />
        ))}
        </div>

    )
}
